import AnimatedLetter from '../AnimatedLetter'
import './index.scss'
import Loader from 'react-loaders'
import { useEffect, useRef, useState } from 'react'
import emailjs from '@emailjs-com'

const Contact = () => {
  const [letterClass, setLetterClass] = useState('text-animate')
  const refForm = useRef()

  useEffect(() => {
    setTimeout(() => {
      return setLetterClass('text-animate-hover')
    }, 4000)
  }, [])

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm("service_w35jr8u", "template_q3o7for", refForm.current, '3q1Utfo3iBzn0fhrL');


    //   (response) => {
    //   alert('SUCCESS!', response.status)
    //   window.location.reload(false);
    // },
    //   (error) => {
    //     alert('FAILED...', error);
    //   }

}

  return (
    <>
      <div className="container contact">
        <div className="text-zone">
          <h1>
            <AnimatedLetter
              letterClass={letterClass}
              strArray={['C', 'o', 'n', 't', 'a', 'c', 't', '', 'M', 'e']}
              idx={15}
            />
          </h1>
          <p>
            I am interested in freelence opportunities - especially ambitious or
            large projects. However, if you have other request or question ,
            don't hesitate t contact me using below form either.
          </p>
          <div className="contact-form">
            <form ref={refForm} onSubmit={sendEmail}>
              <ul>
                <li className="half">
                  <input type="text" name="name" placeholder="Name" required />
                </li>
                <li className="half">
                  <input
                    type="email"
                    name="email"
                    placeholder="Email"
                    required
                  />
                </li>
                <li>
                  <input
                    placeholder="Subject"
                    type="text"
                    name="subject"
                    required
                  />
                </li>
                <li>
                  <textarea
                    placeholder="Message"
                    name="message"
                    required
                  ></textarea>
                </li>
                <li>
                  <input type="submit" className="flat-button" value="Send" />
                </li>
              </ul>
            </form>
          </div>
        </div>
      </div>

      <Loader type="pacman"></Loader>
    </>
  )
}

export default Contact
